package ktor.test

import io.ktor.server.engine.*
import io.ktor.server.netty.*

class App {
  fun start(args: Array<String>) {
    embeddedServer(Netty, commandLineEnvironment(args)).start(wait = true)
  }
}

fun main(args: Array<String>) {
  App().start(args)
}
